@SuppressLint("StateFlowValueCalledInComposition")
@Composable
fun HomeScreen(
    navController: NavHostController,
    viewModel: ViewModel = hiltViewModel()
) {

    val currentDocument by viewModel.currentDocument.collectAsState()
    var bodyPartSearch by remember { mutableStateOf("") }    // Longitude state
    val isComplete by viewModel.isComplete.collectAsState()

    Scaffold(
    ) {
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(it),
            verticalArrangement = Arrangement.Center,
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            Box(
                modifier = Modifier.weight(1f),
                contentAlignment = Alignment.Center
            ) {

                Column(
                    Modifier.fillMaxWidth(),
                    horizontalAlignment = Alignment.CenterHorizontally
                ) {

                    Text(
                        text = "Home Screen",
                        color = Color.DarkGray
                    )

                    Box{
                        TheBodyGraphicView()
                    }


                    if(isComplete){
                        Spacer(Modifier.height(16.dp))
                        Text(
                            text = "Current Document = ${currentDocument.getName()}",
                            color = Color.DarkGray
                        )
                        Spacer(Modifier.height(16.dp))
                    }

                    TextField(
                        value = bodyPartSearch,
                        onValueChange = { bodyPartSearch = it },
                        label = {
                            Text(
                                text = "Search body part",
                                style = TextStyle(
                                    fontSize = 20.sp,
                                    fontWeight = FontWeight.Medium
                                )
                            )
                        },
                        //keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number),
                        maxLines = 1,
                        textStyle = TextStyle(
                            color = Color.Black,
                            fontWeight = FontWeight.Bold
                        ),
                        colors = TextFieldDefaults.colors(
                            focusedContainerColor = Color.White,
                            unfocusedContainerColor = Color.White,
                            focusedLabelColor = Color.DarkGray,
                            unfocusedLabelColor = Color.DarkGray
                        ),
                        modifier = Modifier
                            .fillMaxWidth()
                            .padding(8.dp)
                    )


                    Spacer(Modifier.height(16.dp))

                    Button(
                        onClick = { viewModel.startService(
                            setOf(TypeOfService.Wikipedia,TypeOfService.Youtube),bodyPartSearch
                        ) }
                    ) {

                        Text(
                            text = "Run both Wikipedia and Youtube services",
                            color = Color.White
                        )
                    }

                    Spacer(Modifier.height(16.dp))

                    Button(
                        onClick = { navController.navigate(Screen.WikipediaScreen.route) }
                    ) {

                        Text(
                            text = "Wikipedia screen",
                            color = Color.White
                        )
                    }

                    Spacer(Modifier.height(16.dp))

                    Button(
                        onClick = { navController.navigate(Screen.VideoScreen.route) }
                    ) {

                        Text(
                            text = "Video Screen",
                            color = Color.White
                        )
                    }


                }
            }
        }
    }
}
